import React, { useState } from 'react';
import Header from './Header';
import ChannelList from '../channels/ChannelList';
import MenuBar from './MenuBar';

function Layout({ children, setIsAuthenticated }) {
  const [activeView, setActiveView] = useState('channels');

  return (
    <div className="app-container">
      <Header setIsAuthenticated={setIsAuthenticated} />
      <div className="main-content">
        <MenuBar setActiveView={setActiveView} />
        {activeView === 'channels' && <ChannelList />}
        {activeView === 'messages' && <div>Messages View</div>}
        {activeView === 'replies' && <ReplyThread />}
        <main className="channel-content">
          {children}
        </main>
      </div>
    </div>
  );
}

export default Layout;
