import React, { useState } from 'react';
import Header from './Header';
import ChannelList from '../channels/ChannelList';
import MenuBar from './MenuBar';
import ReplyThread from '../messages/ReplyThread';

function Layout({ children, setIsAuthenticated }) {
  const [activeView, setActiveView] = useState('messages');
  const [selectedMessageId, setSelectedMessageId] = useState(null);

  const handleReplyClick = (messageId) => {
    setSelectedMessageId(messageId);
    setActiveView('replies');
  };

  return (
    <div className="app-container">
      <Header setIsAuthenticated={setIsAuthenticated} />
      <MenuBar setActiveView={setActiveView} />
      <div className="main-content">
        <div className={`sidebar ${activeView === 'channels' ? 'mobile-show' : ''}`}>
          <ChannelList />
        </div>
        <div className={`content ${activeView === 'messages' ? 'mobile-show' : ''}`}>
          <main className="channel-content">
            {React.cloneElement(children, { onReplyClick: handleReplyClick })}
          </main>
        </div>
        <div className={`thread-panel ${activeView === 'replies' ? 'mobile-show' : ''}`}>
          {activeView === 'replies' ? (
            selectedMessageId ? (
              <ReplyThread messageId={selectedMessageId} />
            ) : (
              <div className="thread-placeholder">
                Select a message to view its replies
              </div>
            )
          ) : null}
        </div>
      </div>
    </div>
  );
}

export default Layout;
