import React, { useState } from 'react';
import { authFetch } from '../../utils/api';

function MessageInput({ channelId, replyTo, placeholder = "Type a message..." }) {
  const [content, setContent] = useState('');

  const handleSendMessage = async () => {
    if (!content.trim()) return;

    try {
      const response = await authFetch(`http://127.0.0.1:5000/api/channels/${channelId}/messages`, {
        method: 'POST',
        body: JSON.stringify({ content, replyTo })
      });

      if (response.ok) {
        setContent('');
        // Optionally handle the new message response
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="message-input">
      <input
        type="text"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder={placeholder}
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
}

export default MessageInput;
